-- 迁移 077：将前端 locale 中的隐私政策、用户协议、Cookie 政策全文写入 legal_documents
-- 依赖：须先执行 076_add_legal_documents.sql（建表并插入 6 条占位行）
-- 由脚本根据 frontend/src/locales/zh.json 与 en.json 生成；重复执行仅覆盖 content_json，幂等

UPDATE legal_documents SET content_json = $LEGAL077_privacy_zh${"title": "隐私通知", "lastUpdated": "最后更新：2026年1月", "version": "版本：v1.0", "effectiveDate": "生效日期：2026年1月", "controller": "控制者（Controller）", "operator": "LINK2UR LTD", "contactEmail": "联系邮箱：info@link2ur.com", "address": "通讯地址：A3, B16 9NB, UK", "dpoNote": "（如需数据保护联系人/DPO，可使用以上邮箱）", "dataCollection": {"title": "我们收集哪些数据 & 处理目的（法律依据）", "accountData": "账户数据：邮箱、姓名（如提供）、登录记录等 —— 用于创建与管理账户、与您沟通（合同履行/合法利益）。", "taskData": "任务、跳蚤市场、论坛与沟通数据：任务与二手商品发布内容、时间戳、站内消息、论坛发帖与回复及相关沟通记录 —— 用于撮合、展示、风控与支持（合同履行/合法利益）。", "locationData": "位置信息：当您使用地图选点或允许应用访问位置时，我们会收集坐标（经纬度）。我们仅展示模糊位置（如城市或区域）。位置用于：（1）选择任务/商品位置、距离与匹配；（2）基于位置的推荐；（3）优化推荐算法。您可手动输入位置，定位非必选。GPS 数据仅用于推荐，不与第三方共享（合同履行/合法利益）。", "recommendationData": "推荐系统数据：我们收集浏览与搜索行为、交互数据（您查看、接受、跳过或收藏的任务/商品）、偏好与历史数据，以提供个性化任务与内容推荐。您可在设置中退出个性化推荐（合法利益）。", "technicalData": "技术与日志数据：IP、设备信息、应用版本（网页与 iOS/Android）、错误日志、必要 Cookies —— 用于安全、调试与防滥用（合法利益）。", "analyticsData": "可选分析 Cookies（如启用）：用于了解使用情况与改进体验（同意）。", "paymentData": "支付相关：我们通过 Stripe（银行卡、Apple Pay、微信支付等）处理支付。我们收集支付金额、状态、时间及相关 ID 用于对账与支持。完整支付凭证由 Stripe/Apple/微信处理，我们不予存储。通过 Apple App Store 购买的 VIP 订阅适用 Apple 条款与隐私政策；我们仅接收订阅状态用于访问控制（合同履行/合法利益）。"}, "dataSharing": {"title": "与谁共享", "cloudServices": "云服务/托管与安全供应商：仅在提供服务所必需的范围内共享，并签署数据处理条款。", "paymentProcessors": "支付与订阅处理方（如 Stripe、Apple、微信支付）：仅在处理支付与 VIP 订阅所必需的范围内共享；各方另有其隐私政策。", "legalDisclosure": "执法/监管与纠纷处理：在法律要求或为保护合法权利时披露必要信息。"}, "internationalTransfer": {"title": "国际传输", "content": "如需将个人数据传输至英国以外，我们会采用适用的传输保障（如 UK IDTA 或欧盟 SCCs 的英国附加条款等）。"}, "retentionPeriod": {"title": "保留期限", "accountData": "账户与基础运营数据：账户存续期 + 合理的备份期。", "transactionData": "交易/任务与客服相关记录：为会计、税务或合规目的，通常保留最长不超过6年（除非法律要求更长/更短）。", "recommendationData": "推荐系统数据：用户浏览行为、搜索记录、交互数据等用于推荐的数据，通常保留不超过2年，或直到您删除账户。GPS位置数据仅临时使用，不长期存储。", "securityLogs": "安全日志：基于风险分级确定，尽量最短必要。"}, "yourRights": {"title": "您的权利", "content": "您有权请求访问、更正、删除、限制处理、数据可携、反对处理，以及在基于同意时撤回同意（撤回不影响撤回前的处理合法性）。", "complaintProcess": "如对我们的数据处理有疑问或投诉，请先邮件联系 info@link2ur.com；仍有疑虑，您可向英国信息监管机构 ICO 投诉。"}, "cookies": {"title": "Cookies", "necessary": "必要 Cookies：为站点运行所必需。", "optional": "分析/功能性 Cookies（可选）：仅在您明确同意后启用；您可随时在 Cookie 横幅或设置中更改偏好。"}, "contactUs": {"title": "联系我们", "content": "就本隐私通知或个人数据事宜，随时联系：info@link2ur.com"}, "importantNotice": "重要提示：本隐私通知是您与Link²Ur之间关于个人信息处理的协议。我们承诺按照本通知保护您的隐私权益。"}$LEGAL077_privacy_zh$::jsonb, version = 'v1.0' WHERE type = 'privacy' AND lang = 'zh';

UPDATE legal_documents SET content_json = $LEGAL077_terms_zh${"title": "用户协议", "lastUpdated": "最后更新：2026年1月", "version": "版本：v1.0", "effectiveDate": "生效日期：2026年1月", "jurisdiction": "适用法域：英格兰与威尔士", "operatorInfo": "主体信息", "operator": "运营方（\"我们\"\"Link²Ur\"）：LINK2UR LTD", "contact": "联系：info@link2ur.com", "serviceNature": {"title": "1. 服务性质", "content": "Link²Ur 是面向英国用户的本地服务平台，包括：（1）任务市场——连接任务发布者与任务接受者；（2）跳蚤市场——二手物品买卖；（3）社区论坛——按大学分类的讨论区；（4）VIP 会员——可选自动续期订阅（通过 Apple App Store 等渠道的应用内购买）。我们提供撮合与发布服务，并通过 Stripe（包括银行卡、Apple Pay、微信支付等）处理平台内支付。我们可随时变更、暂停或终止访问。", "testingPhase": "测试阶段声明：当前平台处于测试阶段，功能可能不完善，服务可能不稳定。用户使用本平台即表示理解并接受测试阶段可能存在的风险。", "paymentSystem": "支付系统：平台使用 Stripe 支付系统处理在线支付。所有支付均通过 Stripe 安全处理，平台作为托管方，在任务完成后将资金转给任务接受者。", "recommendationSystem": "个性化推荐系统：平台提供基于用户行为、偏好和地理位置的个性化任务推荐服务。推荐算法会分析您的浏览历史、搜索记录、任务接受偏好、常去地点等信息，为您推荐更符合您兴趣和需求的任务。您可以通过设置选择退出个性化推荐，但退出后可能影响推荐质量。推荐结果仅供参考，平台不对推荐结果的准确性或适用性承担责任。"}, "userTypes": {"title": "2. 用户类型与资格", "content": "仅限年满18岁的用户注册与使用。", "userTypes": "用户可能为企业用户（B2B）或个人/消费者（B2C）。如属消费者，适用文末《消费者条款附录》。", "workEligibility": "工作资格要求：用户在接受任务前，必须确保自己拥有合法的工作资格。这包括但不限于：持有有效的工作签证、拥有相关行业所需的许可证或资质证书、符合当地法律法规对从事相关工作的要求。用户有责任自行确认并遵守所有适用的法律法规。平台不对用户的工作资格进行验证，但保留要求用户提供相关证明的权利。"}, "platformPosition": {"title": "3. 平台定位与禁止站外/线下交易", "content": "我们是撮合型在线平台，不是任何任务/项目的雇主、承包商或代理。平台仅提供信息撮合服务，不参与实际交易执行。", "offPlatform": "禁止站外交易与线下自行协商：平台禁止用户进行站外交易及线下自行协商。站外交易与线下自行协商容易产生纠纷、存在资金与履约风险，且平台无法提供担保与争议处理。用户不得引导或促成在平台外支付、线下交易或绕过平台争议解决（包括但不限于在聊天中留联系方式后线下付款、引导对方使用平台外支付方式等）。违规可能导致内容删除、限制功能或封号。若用户仍进行站外或线下交易，由此产生的合同、质量、交付、退款、税费与纠纷均由交易双方自行承担与处理，平台不承担责任或担保。", "employmentStatus": "雇佣关系声明：平台与用户之间不存在雇佣关系。用户以自雇/个体户形式提供服务，用户对其提供的服务承担全部责任，包括但不限于服务质量、税务申报、保险等。平台不对用户的服务质量、安全或任何损失承担责任。", "offlineTransactions": "线下交易禁止：平台禁止线下自行协商与线下交易。若用户仍进行线下交易，平台不承担任何责任；用户应自行评估并承担交易风险，平台不对交易结果、服务质量、资金安全等承担任何责任。", "scopeOfServices": "各业务适用范围（请务必知悉）：\n• 任务市场：须通过本平台完成支付与争议处理；禁止站外交易与线下自行协商。本协议中的担保支付（资金托管）、退款政策、争议与仲裁规则仅适用于通过本平台完成支付的任务交易。\n• 跳蚤市场：平台仅提供信息发布与撮合；禁止站外交易与线下自行协商，用户须在平台内沟通，不得引导对方线下付款或私下交易。平台不提供支付与资金托管，若用户仍进行站外/线下交易，平台不对交易结果、资金安全、商品质量承担责任。\n• 社区论坛：按大学/学校分类的讨论区，仅提供信息发布与交流，用户对发帖内容负责；平台不参与用户间纠纷调解（法律要求除外）。\n• VIP 会员：可选自动续期订阅，通过 Apple App Store 等渠道购买，适用各渠道的条款与退款政策；VIP 权益（如任务展示优先等）以平台公示为准。"}, "feesAndRules": {"title": "4. 费用与平台规则", "content": "平台服务费：平台会从每笔任务支付中收取一定比例的服务费（具体费率以平台公示为准）。服务费在支付时自动扣除，剩余金额将作为托管金额，在任务完成后转给任务接受者。", "reviews": "评价与内容：禁止虚假/有误导性的评价、刷评或组织\"好评任务\"。我们可删除相关内容并限制或终止账号。", "paymentProcessing": "支付处理：所有支付通过 Stripe 支付系统处理。平台作为托管方，在任务发布者支付后，资金将托管在平台账户中，待任务完成后转给任务接受者。", "applicationFee": "平台服务费：平台会从任务金额中扣除服务费。服务费计算规则如下：\n• 微型任务（任务金额 < 10镑）：固定收取 1 镑作为微型任务服务费\n• 普通任务（任务金额 ≥ 10镑）：按任务金额的 10% 收取服务费\n服务费包括但不限于：支付转账手续费、平台运营成本（服务器、带宽、系统维护等）、人力成本（客服支持、技术开发、运营管理等）、风险保障金以及其他平台运营所需的合理费用。服务费用于维护平台运营、提供技术支持和服务保障。具体费率以平台公示为准。"}, "paymentAndRefund": {"title": "5. 支付与退款", "paymentMethod": "支付方式：平台支持通过 Stripe 支付系统进行在线支付，包括信用卡、借记卡等多种支付方式。", "paymentProcess": "支付流程：任务发布者在接受申请后，需要通过 Stripe 支付系统完成支付。支付成功后，资金将托管在平台账户中。", "escrowService": "资金托管：平台提供资金托管服务，确保任务发布者的资金安全。资金将在任务完成后转给任务接受者。", "refundPolicy": "退款政策：", "completionConfirmation": "任务确认完成步骤：任务完成后，任务发布者需要确认任务完成。确认完成后，资金将从托管账户转给任务接受者。确认完成是一个重要的时间节点，将影响退款政策的适用。", "refundBeforeConfirmation": "确认完成前的退款政策：在任务发布者确认任务完成之前，任务发布者可以申请退款。退款申请需要提供双边证据，包括但不限于：（1）任务状态截图；（2）双方沟通记录（站内消息、邮件等）；（3）任务描述与实际交付结果的对比；（4）时间证明（如任务截止时间与实际完成时间的对比）；（5）其他相关证据。平台将在收到申请后 3-5 个工作日内进行审核，必要时可能联系双方了解情况。如果双方无法达成一致，平台将根据任务完成情况、沟通记录、证据材料等因素进行裁决。", "appealAfterConfirmation": "确认完成后的申诉政策：任务发布者确认任务完成后，原则上不再接受退款申请。如果任务发布者认为存在严重问题（如任务接受者存在欺诈、违约等严重行为），可以向平台提出申诉。申诉需要提供充分的证据，证明任务接受者存在严重违规行为。平台将根据申诉内容和证据进行审核，但不保证能够追回款项。平台保留根据具体情况决定是否处理申诉的权利，且不承担任何追回款项的保证或责任。", "refundEligibility": "退款资格：只有在以下情况下，任务发布者才有资格申请退款：", "refundConditions": "（1）任务尚未开始：任务状态为 pending_payment（待支付）或 in_progress（进行中）但任务接受者尚未实际开始工作。\n（2）任务未完成且未验收：任务接受者未能按照约定完成任务，且任务发布者未确认任务完成（任务状态为 pending_confirmation 或 in_progress）。\n（3）任务质量不符合要求：任务已完成但质量明显不符合任务描述或双方约定，且任务发布者能够提供充分证据。\n（4）任务接受者违约：任务接受者违反协议约定，如未按时完成、中途放弃、提供虚假服务等。", "refundEvidence": "退款申请需要提供的证据：", "refundEvidenceList": "• 任务状态截图：显示任务当前状态（pending_payment、in_progress、pending_confirmation 等）\n• 沟通记录：与任务接受者的站内消息、邮件或其他沟通记录，证明任务未开始、未完成或质量不符合要求\n• 任务描述对比：如果因质量不符合要求申请退款，需提供任务描述与实际交付结果的对比证据\n• 时间证明：如果因未按时完成申请退款，需提供任务截止时间与实际完成时间的对比\n• 其他相关证据：根据具体情况，平台可能要求提供其他相关证据", "refundProcess": "退款流程：", "refundProcessSteps": "（1）提交申请：任务发布者通过平台提交退款申请，并上传相关证据。\n（2）平台审核：平台将在收到申请后 3-5 个工作日内进行审核，必要时可能联系双方了解情况。\n（3）双方协商：如果任务已开始，平台将尝试促成双方协商解决。如果双方达成一致，按协商结果处理。\n（4）平台裁决：如果双方无法达成一致，平台将根据任务完成情况、沟通记录、证据材料等因素进行裁决。\n（5）退款处理：如果退款申请获得批准，平台将在 5-10 个工作日内通过原支付方式退回款项（扣除已产生的平台服务费）。", "refundTimeLimit": "退款时效：", "refundTimeLimitDetails": "• 任务开始前：可在任务开始前随时申请退款\n• 任务进行中：可在任务状态为 in_progress 或 pending_confirmation 时申请退款\n• 确认完成前：在任务发布者确认任务完成之前，可以申请退款，需要提供双边证据\n• 确认完成后：任务发布者确认任务完成后，原则上不再接受退款申请，只能提出申诉，但不保证能追回款项", "nonRefundable": "不予退款的情况：", "nonRefundableList": "• 任务已完成且任务发布者已确认完成（is_confirmed = 1）\n• 任务发布者因个人原因改变主意（如不再需要该服务）\n• 任务发布者未能提供充分的退款证据\n• 任务接受者已按照约定完成工作，但任务发布者不满意（除非有证据证明质量明显不符合要求）\n• 因不可抗力导致的任务无法完成（如自然灾害、政府政策变化等）", "refundWarning": "重要提示：任务确认完成是一个不可逆的步骤。一旦确认完成，资金将转给任务接受者，退款将变得非常困难。请在确认完成前仔细检查任务完成情况，确保任务符合您的要求。", "partialRefund": "部分退款：", "partialRefundDetails": "在某些情况下，平台可能批准部分退款：\n• 任务部分完成：如果任务接受者已完成部分工作，平台可能根据已完成部分的价值，批准部分退款\n• 质量部分不符合：如果任务质量部分不符合要求，但仍有部分价值，平台可能批准部分退款\n• 双方协商一致：如果双方就部分退款达成一致，平台将按协商结果处理", "refundDispute": "争议处理：", "refundDisputeDetails": "如果双方对退款有争议：\n（1）平台将首先尝试促成双方协商解决\n（2）如果协商不成，平台将根据以下因素进行裁决：\n  • 任务完成情况（是否完成、完成质量）\n  • 双方沟通记录（是否有明确约定、是否有争议）\n  • 证据材料（是否充分、是否可信）\n  • 平台规则和公平原则\n（3）平台裁决为最终决定，双方应遵守\n（4）如果对平台裁决有异议，可通过法律途径解决", "refundProcessing": "退款处理：", "refundProcessingDetails": "• 所有退款将通过原支付方式退回（如原支付方式不可用，平台将联系用户提供其他退款方式）\n• 退款处理时间通常为 5-10 个工作日（从退款申请批准之日起）\n• 平台服务费将从退款金额中扣除（如果任务已部分完成，服务费按比例扣除）\n• 退款成功后，平台将通过邮件或站内消息通知用户", "chargeback": "拒付处理：", "chargebackDetails": "如果发生信用卡拒付（chargeback）：\n• 平台将配合支付处理方（Stripe）进行调查\n• 平台将提供相关交易记录、沟通记录等证据\n• 根据调查结果，平台将采取相应措施\n• 如果拒付被判定为恶意拒付，平台保留追究法律责任的权利", "paymentSecurity": "支付安全：平台使用 Stripe 支付系统，符合 PCI DSS 安全标准，确保支付信息安全。平台不会存储您的完整支付凭证。", "guaranteePayment": "担保支付（资金托管）说明", "guaranteePaymentIntro": "平台对通过本平台完成的任务支付提供担保交易（资金托管）服务：任务发布者支付的款项在任务完成或争议解决前由平台代为保管，不直接支付给任务接受者，以降低交易风险。", "guaranteeWhat": "平台担保的范围：\n• 资金安全保管：支付成功后至释放前，资金由平台在合规支付渠道（如 Stripe）下托管，不提前释放给任一方\n• 按规则释放：仅在（1）任务发布者确认任务完成，或（2）平台根据争议处理规则作出裁决后，按裁决结果释放或退还资金\n• 支付信息安全：通过 Stripe 等符合 PCI DSS 标准的支付处理方处理，平台不存储完整支付凭证", "guaranteeWhatNot": "平台不担保的范围：\n• 任务质量、交付结果或服务效果：平台不对任务接受者提供的服务质量、及时性、合规性作任何保证\n• 站外/线下交易：站外交易与线下自行协商为平台禁止行为，平台不提供担保也不承担责任（详见「平台定位与禁止站外/线下交易」）\n• 不可抗力或第三方原因导致的支付/到账延迟：在合理范围内配合处理，但不承担超出本协议责任上限的赔偿责任", "fundReleaseConditions": "资金释放条件：\n• 正常完成：任务发布者在任务完成后在平台内点击「确认完成」，托管资金（扣除平台服务费后）将按约定转给任务接受者\n• 退款/部分退款：在确认完成前，任务发布者可申请退款；若发生争议，经平台调解或裁决后，按裁决结果执行退款或部分退款，再将其余部分释放给任务接受者\n• 争议裁决后：平台作出的争议处理决定（包括款项归属、部分退款比例等）为平台内最终执行依据，双方应配合执行资金释放或退回"}, "prohibitedTasks": {"title": "6. 禁止的任务类型", "introduction": "为维护平台安全、合法性和用户体验，以下类型的任务严格禁止在平台上发布：", "illegalActivities": "（1）违法活动：任何违反英国法律法规或国际法的任务，包括但不限于：", "illegalActivitiesList": "• 毒品交易、非法药物相关服务\n• 武器、枪支、爆炸物交易或相关服务\n• 人口贩卖、性交易、卖淫服务\n• 洗钱、金融诈骗、非法集资\n• 伪造证件、文件、货币\n• 黑客服务、网络攻击、恶意软件开发\n• 侵犯他人知识产权的服务（如盗版软件、盗版内容）", "harmfulContent": "（2）有害内容：可能对用户或社会造成伤害的任务，包括但不限于：", "harmfulContentList": "• 暴力、威胁、恐吓他人的服务\n• 骚扰、跟踪、恶意中伤他人的服务\n• 传播仇恨言论、歧视性内容\n• 涉及未成年人不当内容\n• 虐待动物相关服务", "fraudulentServices": "（3）欺诈性服务：虚假、误导性或欺诈性的任务，包括但不限于：", "fraudulentServicesList": "• 虚假评价、刷单、刷评服务\n• 虚假身份验证、虚假认证服务\n• 考试作弊、代考、论文代写（学术不诚信）\n• 虚假投资、传销、金字塔骗局\n• 虚假中奖、虚假优惠信息", "regulatedServices": "（4）需持证但无资质的服务：需要特定许可证、资质或认证但发布者或接受者未具备相应资质的任务，包括但不限于：", "regulatedServicesList": "• 医疗服务、医疗咨询（需医疗执业资格）\n• 法律服务、法律咨询（需律师执业资格）\n• 金融服务、投资建议（需金融监管机构许可）\n• 建筑、装修服务（需相关建筑资质）\n• 食品加工、餐饮服务（需食品安全许可）\n• 教育服务、培训（需教育机构资质）\n• 驾驶培训（需驾驶教练资格）", "platformAbuse": "（5）平台滥用：利用平台进行不当操作的任务，包括但不限于：", "platformAbuseList": "• 刷单、刷评、虚假交易\n• 套现、洗钱、资金转移\n• 恶意竞争、恶意举报\n• 系统漏洞利用、自动化脚本滥用", "otherProhibited": "（6）其他禁止内容：", "otherProhibitedList": "• 涉及个人隐私信息买卖\n• 涉及他人账户、密码、个人信息\n• 涉及赌博、博彩相关服务\n• 涉及政治敏感内容\n• 其他违反平台规则或社会公序良俗的内容", "enforcement": "执行措施：如发现发布禁止类型的任务，平台将立即删除该任务，并根据违规严重程度对发布者采取警告、限制功能、暂停账户或永久封禁等措施。平台保留向相关监管或执法机构报告的权利。"}, "userBehavior": {"title": "7. 用户行为与禁止事项", "prohibited": "除禁止的任务类型外，用户还不得：（1）发布违法、侵权、欺诈、误导或有害内容；（2）发布需持证却无资质的任务；（3）抓取、攻击、绕过访问控制或试图破坏系统；（4）进行洗钱、制裁规避或其他金融犯罪；（5）恶意刷单、虚假交易、套现等行为；（6）引导或促成站外交易、线下自行协商、在平台外支付或绕过平台争议解决。", "consequences": "如违规，我们可删除内容、限制/终止访问，并在必要时向监管或执法机构报告。", "accountSuspension": "账户封禁政策：如果平台发现或留意到用户有任何违法、违规行为，包括但不限于违反本协议条款、从事非法活动、欺诈、骚扰其他用户、发布虚假信息等，平台有权利也有义务立即对用户进行封号处理。封号可能包括但不限于：临时暂停账户、永久封禁账户、限制账户功能、删除违规内容等。平台将根据违规行为的严重程度采取相应的措施，并保留向相关监管或执法机构报告的权利。用户被封号后，将无法继续使用平台服务，已发布的任务可能被删除，正在进行中的交易可能被终止。"}, "userResponsibilities": {"title": "8. 用户责任与义务", "introduction": "使用本平台服务，用户需承担以下责任与义务：", "accountSecurity": "（1）账户安全责任：", "accountSecurityList": "• 用户有责任妥善保管账户信息（用户名、密码、邮箱等），不得将账户信息泄露给他人\n• 用户有责任确保账户安全，如发现账户被盗用或异常，应立即通知平台并采取相应措施\n• 用户对账户下的所有操作负责，包括但不限于发布任务、接受任务、支付、收款等\n• 用户不得将账户转让、出售、出租给他人使用", "accurateInformation": "（2）信息真实性责任：", "accurateInformationList": "• 用户有责任提供真实、准确、完整的个人信息\n• 用户有责任确保发布的任务信息真实、准确、完整，不得发布虚假、误导性信息\n• 用户有责任在任务描述中明确任务要求、截止时间、报酬等关键信息\n• 用户有责任及时更新个人信息，确保信息的时效性", "legalCompliance": "（3）合法合规责任：", "legalComplianceList": "• 用户有责任遵守所有适用的法律法规，包括但不限于英国法律、国际法\n• 用户有责任确保发布的任务不违反法律法规、不侵犯他人权益\n• 用户有责任确保自己具备完成任务的合法资格（如工作签证、相关许可证等）\n• 用户有责任遵守平台的规则和政策", "taskCompletion": "（4）任务完成责任：", "taskCompletionList": "• 任务发布者责任：\n  - 提供清晰、准确的任务描述和要求\n  - 及时响应任务接受者的询问和沟通\n  - 在任务完成后及时确认任务完成情况\n  - 按照约定支付任务报酬\n• 任务接受者责任：\n  - 仔细阅读任务描述，确保理解任务要求\n  - 按照任务描述和要求完成工作\n  - 按时完成任务（除非双方另有约定）\n  - 保证工作质量符合任务要求\n  - 及时与任务发布者沟通任务进展", "paymentResponsibility": "（5）支付责任：", "paymentResponsibilityList": "• 任务发布者责任：\n  - 在接受申请后及时完成支付\n  - 确保支付账户有足够余额\n  - 不得恶意拒付、虚假支付\n  - 配合平台处理支付相关问题\n• 任务接受者责任：\n  - 提供准确的收款信息（如 Stripe Connect 账户）\n  - 确保收款账户可正常使用\n  - 配合平台处理收款相关问题", "communicationResponsibility": "（6）沟通责任：", "communicationResponsibilityList": "• 用户有责任保持礼貌、专业的沟通态度\n• 用户有责任及时回复对方的询问和消息\n• 用户有责任在任务进行过程中及时沟通任务进展\n• 用户有责任在出现问题时及时沟通，寻求解决方案", "disputeResolution": "（7）争议解决责任：", "disputeResolutionList": "• 用户有责任在出现争议时，首先尝试与对方友好协商解决\n• 用户有责任配合平台的争议调解工作\n• 用户有责任提供真实、充分的证据材料\n• 用户有责任遵守平台的争议处理决定", "contentResponsibility": "（8）内容责任：", "contentResponsibilityList": "• 用户有责任确保发布的内容不侵犯他人知识产权（如版权、商标、专利等）\n• 用户有责任确保发布的内容不侵犯他人隐私权、肖像权等\n• 用户有责任确保发布的内容不包含违法、有害、不当信息\n• 用户对发布的内容承担全部法律责任", "taxResponsibility": "（9）税务责任：", "taxResponsibilityList": "• 用户有责任自行申报和缴纳相关税费（如所得税、增值税等）\n• 用户有责任保留相关交易记录，以备税务部门核查\n• 平台不负责代扣代缴用户税费，也不提供税务建议", "indemnification": "（10）赔偿责任：", "indemnificationDetails": "用户同意赔偿并保护平台、其关联公司、员工、代理免受因以下原因产生的任何索赔、损失、责任、成本和费用（包括合理的律师费）：\n• 用户违反本协议条款\n• 用户违反任何法律法规\n• 用户侵犯他人权益（如知识产权、隐私权等）\n• 用户发布的内容或提供的服务导致的任何损失", "consequences": "违反责任与义务的后果：", "consequencesDetails": "如果用户违反上述责任与义务：\n• 平台有权立即删除相关内容\n• 平台有权限制或终止用户账户\n• 平台有权拒绝处理相关交易或退款申请\n• 平台有权向相关监管或执法机构报告\n• 用户可能承担相应的法律责任和经济损失"}, "intellectualProperty": {"title": "9. 知识产权与用户内容", "platformRights": "平台及其内容的所有权利归我们或许可方所有。", "userContent": "您保留上传内容的权利，但为提供、运营与推广服务之目的，您授予我们全球范围、非独占、免版税、可转授权的使用许可（用于展示、备份、审核、索引与推广）。", "complaints": "侵权投诉按我们的通知—删除流程处理（请通过上述邮箱联系我们）。"}, "privacyData": {"title": "10. 隐私与数据（UK GDPR）", "controller": "我们作为**数据控制者（controller）**处理账户、日志与沟通数据，仅在\"提供服务、保障安全、改进体验\"的范围内处理，详见下方《隐私通知》；您可行使访问、更正、删除、限制处理、数据可携、反对处理、撤回同意等权利；亦可向 ICO 投诉。", "payments": "支付处理：平台使用 Stripe 支付系统处理支付。Stripe 作为支付处理方，会收集和处理必要的支付信息。我们仅接收支付状态和必要的对账信息，不接触完整的支付凭证（如完整信用卡号）。所有支付数据的安全由 Stripe 负责，符合 PCI DSS 标准。"}, "disclaimer": {"title": "11. 免责声明与责任限制", "service": "服务按\"现状（as is） / 可用性（as available）\"提供，不保证不间断或无错误。", "liability": "我们不对间接、后果性、惩罚性损害或利润/商誉损失承担责任。", "limit": "总责任上限：就任何单一或合并索赔，我们的责任以您在过去12个月向我们实际支付的费用总额或**£100（以较高者为限）**为上限；法律强制责任除外（例如因过失导致的人身伤害或死亡）。"}, "termination": {"title": "12. 终止与数据保留", "content": "您或我们可随时终止测试访问。为遵守法定义务与正当目的，我们可能在最短必要期限内保留日志及记录（具体见《隐私通知（Beta）》）。", "effect": "终止不影响既有权利与义务。"}, "disputes": {"title": "13. 争议与适用法律", "negotiation": "用户之间因任务、支付或服务产生的纠纷，双方应先尽量友好协商；协商不成的，可依下列争议与仲裁规则通过平台处理；仍无法解决的，可提交英格兰与威尔士法院管辖。", "law": "本条款受英格兰与威尔士法律管辖并据其解释。", "disputeArbitration": "争议与仲裁规则", "whoCanSubmit": "谁可以提起争议：任务或订单的任一方（任务发布者或任务接受者）认为对方未按约定履行、存在违约或对任务完成情况/款项归属有异议时，可在平台内对该任务/订单提起争议。", "howToSubmit": "提起方式与时限：在对应任务或订单详情页使用「提交争议」功能，填写争议原因并尽量上传证据（如沟通记录、任务描述与交付对比、时间证明等）。建议在任务状态仍为进行中或待确认完成时尽快提交，以便平台及时介入；确认完成后的申诉适用「支付与退款」中的申诉政策。", "platformMediation": "平台调解：平台收到争议后，将先尝试促成双方协商（包括通过站内消息或邮件沟通）。若双方在平台要求的合理期限内达成一致（如退款比例、补充交付等），按双方一致意见处理资金与订单状态。", "platformDecision": "平台裁决：若协商不成或一方未在合理期限内配合，平台将根据现有证据与规则进行裁决。裁决时会考虑：任务描述与约定、双方沟通记录、任务完成情况与证据、本协议及平台公示规则、公平原则。平台有权要求双方补充证据或说明。", "decisionFactors": "裁决考量因素包括但不限于：任务是否已按约定完成、完成质量是否明显不符合约定、是否存在逾期或擅自变更、双方是否有明确书面约定（含站内消息）、证据是否充分可信。平台不保证采纳任一方的主张，以证据与规则为准。", "decisionBinding": "裁决效力：平台就该项争议作出的处理决定（包括但不限于：驳回争议、支持全额/部分退款、支持释放全部或部分款项给任务接受者等）在平台内为最终决定，双方应遵守并配合执行（如确认退款、配合资金释放）。平台将按裁决结果操作资金释放或退款。", "legalRemedy": "法律救济：若一方对平台裁决不服，仍可依适用法律向有管辖权的法院（英格兰与威尔士）寻求救济；但平台已按裁决执行的资金操作不因后续诉讼而自动逆转，除非法院生效裁判另有要求。"}, "forumTerms": {"title": "论坛服务条款", "intro": "平台提供社区论坛功能，按大学/学校分类，允许用户发帖、回复、点赞、收藏等。论坛旨在促进信息交流与经验分享。", "contentResponsibility": "内容责任：用户对在论坛中发布的全部内容（文字、图片、链接等）承担全部责任，须确保不违反法律法规、不侵犯他人知识产权或隐私、不包含诽谤/骚扰/仇恨言论/歧视/虚假信息/垃圾广告。", "moderation": "内容审核与删除：我们保留审核、编辑、删除或拒绝发布任何论坛内容的权利，无需事先通知。可能因违规、侵权、不当内容或社区标准等原因删除内容。", "conduct": "用户不得在论坛中刷屏、恶意攻击或骚扰他人、冒充他人、发布危害安全的内容、利用论坛从事非法活动或破坏系统。违规将导致内容删除或账号限制。", "disclaimer": "论坛免责：平台不对论坛中的用户生成内容承担责任。用户间争议由用户自行解决；平台不参与调解（法律要求除外），不对因使用或无法使用论坛服务造成的直接或间接损失承担责任。"}, "fleaMarketTerms": {"title": "跳蚤市场服务条款", "intro": "平台提供跳蚤市场功能，允许用户发布、浏览、购买和出售二手商品。跳蚤市场旨在促进用户间的二手交易。", "platformPosition": "平台定位：平台仅提供信息发布与撮合，不参与实际交易过程。禁止站外交易与线下自行协商；用户须在平台内沟通，不得引导对方线下付款或私下交易。平台不提供支付、物流或资金托管服务；若用户仍进行站外/线下交易，平台不对交易结果、商品质量、资金安全承担责任。", "sellerResponsibility": "卖家须确保商品信息真实、准确、完整；不得发布虚假信息或隐瞒重要缺陷。禁止交易法律法规禁止的商品、假冒伪劣、侵权商品、危险品等（详见平台规则）。", "riskNotice": "交易风险提示：所有交易由买卖双方自行完成。平台不对商品质量、真伪、交付、物流或交易纠纷承担责任。建议双方充分沟通、尽量当面交易并保留凭证；高价值物品建议使用安全支付方式。", "disclaimer": "跳蚤市场免责：平台不对跳蚤市场中的任何交易承担责任。因交易产生的纠纷、损失由交易双方自行解决。平台不对因使用或无法使用跳蚤市场服务造成的任何直接或间接损失承担责任。"}, "consumerAppendix": {"title": "消费者条款附录（如用户为消费者时适用）", "freeService": "您作为消费者使用我们的免费撮合服务时，我们会在界面提供关键信息（平台身份、联系方式、主要功能与任何重要限制）。", "futureCharges": "目前测试期我们不向消费者收费，因此不触发付费数字服务的冷静期/退款机制；一旦未来对消费者收费，我们将遵守相关消费者法规（包括信息披露、取消权与禁止隐藏费用/虚假评论等），并在收费前以显著方式告知。"}, "importantNotice": "重要提示：请仔细阅读本协议，特别是免除或限制责任的条款。如果您不同意本协议的任何条款，请停止使用本平台服务。"}$LEGAL077_terms_zh$::jsonb, version = 'v1.0' WHERE type = 'terms' AND lang = 'zh';

UPDATE legal_documents SET content_json = $LEGAL077_cookie_zh${"title": "Cookie 政策", "version": "版本：v1.0", "effectiveDate": "生效日期：2026年1月", "jurisdiction": "适用法域：英格兰与威尔士", "intro": {"title": "简介", "p1": "本 Cookie 政策说明了 Link²Ur（\"我们\"、\"我们的\"）如何使用 Cookies 和类似技术，以及您如何管理这些技术。本政策是《隐私通知》的补充，应与《隐私通知》一起阅读。", "p2": "根据英国《隐私与电子通信条例》（Privacy and Electronic Communications Regulations, PECR）和《通用数据保护条例》（UK GDPR），我们必须在您访问我们的网站时告知您我们使用的 Cookies，并获得您对非必要 Cookies 的同意。"}, "whatAreCookies": {"title": "1. 什么是 Cookies 和类似技术", "p1": "Cookies 是当您访问网站时存储在您设备（计算机、平板电脑或移动设备）上的小型文本文件。Cookies 包含信息，这些信息会在您返回网站或访问其他使用相同 Cookies 的网站时被传输回网站。", "p2": "除了 Cookies，我们还可能使用其他类似技术，包括：", "p3": "• 本地存储（Local Storage）：在您的浏览器中存储数据，类似于 Cookies，但可以存储更多信息。", "p4": "• 会话存储（Session Storage）：临时存储数据，仅在浏览器会话期间有效。", "p5": "• 像素标签（Pixel Tags）：用于跟踪网页访问和电子邮件打开情况的小型图像文件。", "p6": "• 设备标识符：在移动应用中用于识别设备的唯一标识符。"}, "typesWeUse": {"title": "2. 我们使用的 Cookies 类型", "p1": "我们根据 Cookies 的用途和必要性将其分为以下几类：", "p2": "2.1 严格必要的 Cookies（必需）", "p3": "这些 Cookies 对网站的基本功能至关重要，没有这些 Cookies，网站将无法正常运行。这些 Cookies 不需要您的同意即可使用。包括：", "p4": "• 身份验证 Cookies：用于保持您的登录状态，确保您已通过身份验证。", "p5": "• 安全 Cookies：用于防止欺诈、滥用和未经授权的访问，保护您的账户安全。", "p6": "• 负载均衡 Cookies：用于在多个服务器之间分配流量，确保网站稳定运行。", "p7": "• 购物车/会话 Cookies：用于记住您在网站上的操作，如添加到购物车或表单填写进度。", "p8": "2.2 功能性 Cookies（可选）", "p9": "这些 Cookies 允许网站记住您的选择（如语言偏好、地区设置），以提供增强的个性化功能。这些 Cookies 仅在您明确同意后启用。包括：", "p10": "• 偏好设置 Cookies：记住您的语言、主题、字体大小等设置。", "p11": "• 功能性 Cookies：记住您的搜索历史、收藏内容、最近查看的项目等。", "p12": "• 地理位置 Cookies：记住您的位置偏好，用于提供本地化的内容和服务。", "p13": "2.3 分析 Cookies（可选）", "p14": "这些 Cookies 帮助我们了解用户如何使用网站，以便我们改进网站性能和用户体验。这些 Cookies 仅在您明确同意后启用。包括：", "p15": "• 网站分析 Cookies：用于统计访问量、页面浏览量、用户停留时间、跳出率等指标。", "p16": "• 性能监控 Cookies：用于监控网站性能、错误率、加载时间等，帮助我们优化网站速度。", "p17": "• 用户行为分析 Cookies：用于分析用户在网站上的行为模式，了解用户偏好和需求。", "p18": "我们可能使用第三方分析服务（如 Google Analytics），这些服务提供商有自己的隐私政策。我们建议您查看这些第三方的隐私政策以了解他们如何使用 Cookies。", "p19": "2.4 广告 Cookies（目前未使用）", "p20": "目前我们不在网站上使用广告 Cookies。如果未来我们使用广告 Cookies，我们将在本政策中更新相关信息，并在使用前获得您的明确同意。"}, "thirdParty": {"title": "3. 第三方 Cookies", "p1": "我们可能允许第三方服务提供商在我们的网站上设置 Cookies。这些第三方 Cookies 受第三方隐私政策的约束，我们不对其负责。", "p2": "我们可能使用的第三方服务包括：", "p3": "• 分析服务：如 Google Analytics，用于网站分析和性能监控。", "p4": "• 云服务提供商：用于托管和存储数据。", "p5": "• 安全服务：用于防止欺诈和滥用。", "p6": "我们建议您查看这些第三方的隐私政策和 Cookie 政策，以了解他们如何使用 Cookies 和您的数据。"}, "retention": {"title": "4. Cookie 保留期限", "p1": "不同类型的 Cookies 有不同的保留期限：", "p2": "• 会话 Cookies：这些 Cookies 是临时的，仅在您访问网站期间有效。当您关闭浏览器时，这些 Cookies 会自动删除。", "p3": "• 持久 Cookies：这些 Cookies 会在您的设备上保留一段时间，即使您关闭浏览器也会保留。持久 Cookies 的保留期限通常为 30 天至 2 年，具体取决于 Cookie 的类型和用途。我们会在 Cookie 设置页面提供每种 Cookie 的具体保留期限信息。", "p4": "• 第三方 Cookies：第三方 Cookies 的保留期限由第三方服务提供商决定，我们无法控制。请查看相关第三方的隐私政策了解详细信息。"}, "howToManage": {"title": "5. 如何管理 Cookies", "p1": "您可以通过以下方式管理 Cookies：", "p2": "5.1 通过我们的 Cookie 设置", "p3": "您可以通过我们网站上的 Cookie 设置页面随时更改您的 Cookie 偏好。您可以启用或禁用功能性 Cookies 和分析 Cookies，但请注意，禁用某些 Cookies 可能会影响网站的功能和您的使用体验。", "p4": "5.2 通过浏览器设置", "p5": "大多数浏览器允许您管理 Cookies。您可以通过浏览器设置：", "p6": "• 查看您设备上存储的 Cookies", "p7": "• 删除 Cookies（包括所有 Cookies 或特定网站的 Cookies）", "p8": "• 阻止 Cookies（阻止所有 Cookies 或仅阻止第三方 Cookies）", "p9": "• 在设置 Cookies 前收到通知", "p10": "请注意，禁用或删除 Cookies 可能会影响网站的功能。某些功能可能无法正常工作，您可能需要重新输入信息或重新设置偏好。", "p11": "5.3 移动应用中的 Cookie 管理", "p12": "在移动应用中，您可以通过应用设置管理类似 Cookies 的技术（如应用内标识符、设备 ID 等）。您也可以通过在设备设置中重置广告标识符来管理某些标识符。"}, "mobileTech": {"title": "6. 移动应用中的类似技术", "p1": "在移动应用中，我们可能使用类似 Cookies 的技术来实现类似的功能，包括：", "p2": "• 应用内标识符：用于识别应用安装和用户会话。", "p3": "• 设备 ID：用于识别设备，提供个性化服务。", "p4": "• 广告标识符：用于提供相关广告（如果适用）。", "p5": "这些技术的使用遵循我们的《隐私通知》的规定。您可以通过应用设置或设备设置管理这些技术。"}, "yourRights": {"title": "7. 您的权利", "p1": "根据 UK GDPR 和 PECR，您对 Cookies 享有以下权利：", "p2": "• 知情权：您有权了解我们使用哪些 Cookies 以及为什么使用它们。", "p3": "• 同意权：对于非必要的 Cookies，您有权选择是否同意使用。", "p4": "• 撤回同意权：您可以随时撤回对非必要 Cookies 的同意。", "p5": "• 删除权：您可以随时删除存储在您设备上的 Cookies。", "p6": "• 访问权：您可以请求访问我们通过 Cookies 收集的您的个人数据。", "p7": "如果您想行使这些权利，请通过 info@link2ur.com 联系我们。"}, "contactUs": {"title": "8. 联系我们", "p1": "如果您对本 Cookie 政策有任何疑问或需要帮助管理您的 Cookie 偏好，请通过以下方式联系我们：", "p2": "• 电子邮件：info@link2ur.com", "p3": "• 通讯地址：A3, B16 9NB, UK", "p4": "我们会在合理的时间内回复您的询问。"}, "importantNotice": {"title": "重要提示", "p1": "本 Cookie 政策可能会不时更新。我们会在政策更新时通知您，并在本页面上发布更新后的版本。我们建议您定期查看本政策以了解最新信息。", "p2": "最后更新日期：2026年1月"}, "necessary": "必要 Cookie：用于网站基本功能（如登录、安全），无法关闭。", "optional": "分析/功能 Cookie（可选）：仅在您明确同意后启用；您可随时在 Cookie 横幅或设置中更改。", "contact": "如有疑问请联系：info@link2ur.com"}$LEGAL077_cookie_zh$::jsonb, version = 'v1.0' WHERE type = 'cookie' AND lang = 'zh';

UPDATE legal_documents SET content_json = $LEGAL077_privacy_en${"title": "Privacy Notice", "lastUpdated": "Last Updated: January 2026", "version": "Version: v1.0", "effectiveDate": "Effective Date: January 2026", "controller": "Controller", "operator": "LINK2UR LTD", "contactEmail": "Contact Email: info@link2ur.com", "address": "Mailing Address: A3, B16 9NB, UK", "dpoNote": "(For data protection contact/DPO, please use the above email)", "dataCollection": {"title": "What Data We Collect & Processing Purposes (Legal Basis)", "accountData": "Account Data: Email, name (if provided), login records, etc. — For account creation and management, communication with you (contract performance/legitimate interests).", "taskData": "Task, Flea Market, Forum and Communication Data: Task and flea market listing content, timestamps, in-platform messages, forum posts and replies, and related communication records — For matching, listings, risk control and support (contract performance/legitimate interests).", "locationData": "Location Information: When you use the map location picker or allow the app to access location, we collect coordinates (latitude/longitude). We display only obfuscated locations (e.g. city or area). Location is used for: (1) selecting task/item locations, distance and matching; (2) location-based recommendations; (3) optimising recommendation algorithms. You may enter locations manually; location is not mandatory. GPS data is used for recommendation only and not shared with third parties (contract performance/legitimate interests).", "recommendationData": "Recommendation System Data: We collect browsing and search behaviour, interaction data (tasks/items you view, accept, skip or favourite), preference and historical data to provide personalised task and content recommendations. You can opt out of personalised recommendations in settings (legitimate interests).", "technicalData": "Technical and Log Data: IP, device information, app version (web and iOS/Android), error logs, necessary Cookies — For security, debugging and abuse prevention (legitimate interests).", "analyticsData": "Optional Analytics Cookies (if enabled): For understanding usage and improving experience (consent).", "paymentData": "Payment Related: We process payments via Stripe (card, Apple Pay, WeChat Pay where available). We collect payment amount, status, time and relevant IDs for reconciliation and support. Full payment credentials are processed by Stripe/Apple/WeChat; we do not store them. VIP subscription purchases via the Apple App Store are subject to Apple’s terms and privacy policy; we receive only subscription status for access control (contract performance/legitimate interests)."}, "dataSharing": {"title": "Who We Share With", "cloudServices": "Cloud Services/Hosting and Security Vendors: Only shared to the extent necessary for service provision, with data processing agreements in place.", "paymentProcessors": "Payment and Subscription Processors (e.g. Stripe, Apple, WeChat Pay): Only to the extent necessary to process payments and VIP subscriptions; each has its own privacy policy.", "legalDisclosure": "Law Enforcement/Regulatory and Dispute Resolution: Disclose necessary information when legally required or to protect legitimate rights."}, "internationalTransfer": {"title": "International Transfers", "content": "If personal data needs to be transferred outside the UK, we will use applicable transfer safeguards (such as UK IDTA or UK addendum to EU SCCs, etc.)."}, "retentionPeriod": {"title": "Retention Periods", "accountData": "Account and Basic Operational Data: Account duration + reasonable backup period.", "transactionData": "Transaction/Task and Customer Service Records: For accounting, tax or compliance purposes, typically retained for no more than 6 years (unless law requires longer/shorter).", "recommendationData": "Recommendation System Data: User browsing behavior, search records, interaction data and other data used for recommendations are typically retained for no more than 2 years, or until you delete your account. GPS location data is only used temporarily and not stored long-term.", "securityLogs": "Security Logs: Determined based on risk classification, kept for the shortest necessary period."}, "yourRights": {"title": "Your Rights", "content": "You have the right to request access, rectification, erasure, restriction of processing, data portability, objection to processing, and to withdraw consent when based on consent (withdrawal does not affect the legality of processing before withdrawal).", "complaintProcess": "If you have questions or complaints about our data processing, please first contact info@link2ur.com by email; if you still have concerns, you may complain to the UK Information Commissioner's Office (ICO)."}, "cookies": {"title": "Cookies", "necessary": "Necessary Cookies: Essential for site operation.", "optional": "Analytics/Functional Cookies (Optional): Only enabled after your explicit consent; you can change preferences at any time in the Cookie banner or settings."}, "contactUs": {"title": "Contact Us", "content": "For this privacy notice or personal data matters, contact us anytime: info@link2ur.com"}, "importantNotice": "Important Notice: This privacy notice is an agreement between you and Link²Ur regarding personal information processing. We are committed to protecting your privacy rights in accordance with this notice."}$LEGAL077_privacy_en$::jsonb, version = 'v1.0' WHERE type = 'privacy' AND lang = 'en';

UPDATE legal_documents SET content_json = $LEGAL077_terms_en${"title": "Terms of Service", "lastUpdated": "Last Updated: January 2026", "version": "Version: v1.0", "effectiveDate": "Effective Date: January 2026", "jurisdiction": "Applicable Jurisdiction: England & Wales", "operatorInfo": "Entity Information", "operator": "Operator (\"We\" \"Link²Ur\"): LINK2UR LTD", "contact": "Contact: info@link2ur.com", "serviceNature": {"title": "1. Nature of Service", "content": "Link²Ur is a local service platform for users in the UK, comprising: (1) Task Marketplace — connecting task posters with task takers; (2) Flea Market — for buying and selling secondhand items; (3) Community Forum — for discussions by university; (4) VIP Membership — optional auto-renewable subscription (in-app purchase via Apple App Store or other supported channels). We provide matching and listing services and process in-platform payments through Stripe (including card, Apple Pay and WeChat Pay where available). We may change, suspend or terminate access at any time.", "testingPhase": "Testing Phase Statement: The platform is currently in testing phase, features may be incomplete and services may be unstable. Users using this platform acknowledge and accept the risks that may exist during the testing phase.", "paymentSystem": "Payment System: The platform uses the Stripe payment system to process online payments. All payments are securely processed through Stripe, and the platform acts as an escrow service, transferring funds to task takers upon task completion.", "recommendationSystem": "Personalized Recommendation System: The platform provides personalized task recommendation services based on user behavior, preferences, and geographic location. The recommendation algorithm analyzes your browsing history, search records, task acceptance preferences, frequently visited locations, and other information to recommend tasks that better match your interests and needs. You can opt out of personalized recommendations through settings, but this may affect recommendation quality. Recommendation results are for reference only, and the platform does not assume responsibility for the accuracy or suitability of recommendation results."}, "userTypes": {"title": "2. User Types and Eligibility", "content": "Only users aged 18 and above may register and use the service.", "userTypes": "Users may be business users (B2B) or individual/consumer users (B2C). If you are a consumer, the Consumer Terms Appendix at the end applies.", "workEligibility": "Work Eligibility Requirements: Before accepting tasks, users must ensure they have legal work eligibility. This includes but is not limited to: holding a valid work visa, possessing licenses or qualifications required for the relevant industry, and complying with local laws and regulations regarding the performance of related work. Users are responsible for confirming and complying with all applicable laws and regulations. The platform does not verify users' work eligibility, but reserves the right to require users to provide relevant proof."}, "platformPosition": {"title": "3. Platform Position and Prohibition of Off-Platform / Offline Transactions", "content": "We are a matching online platform, not an employer, contractor or agent for any tasks/projects. The platform only provides information matching services and does not participate in actual transaction execution.", "offPlatform": "Prohibition of off-platform and offline transactions: The platform prohibits users from conducting off-platform transactions and offline self-negotiation. Off-platform and offline transactions easily lead to disputes and carry fund and performance risks; the platform cannot provide guarantees or dispute resolution for them. Users must not induce or facilitate payment outside the platform, offline transactions, or bypassing platform dispute resolution (including but not limited to leaving contact details in chat to pay offline, directing the other party to pay outside the platform, etc.). Violations may result in content removal, restricted features or account suspension. If users nevertheless conduct off-platform or offline transactions, any contract, quality, delivery, refund, tax or dispute arising therefrom is solely between the parties; the platform assumes no responsibility or guarantee.", "employmentStatus": "Employment Relationship Statement: There is no employment relationship between the platform and users. Users provide services as self-employed/independent contractors and bear full responsibility for their services, including but not limited to service quality, tax reporting, insurance, etc. The platform is not responsible for users' service quality, safety or any losses.", "offlineTransactions": "Prohibition of offline transactions: The platform prohibits offline self-negotiation and offline transactions. If users nevertheless conduct offline transactions, the platform assumes no responsibility; users should assess and bear transaction risks; the platform bears no responsibility for transaction outcomes, service quality, fund safety, etc.", "scopeOfServices": "Scope of services (please note):\n• Task Marketplace: Payment and dispute resolution must be completed through the platform; off-platform and offline transactions are prohibited. The guaranteed payment (escrow), refund policy and dispute/arbitration rules in these terms apply only to task payments completed through the platform.\n• Flea Market: The platform only provides listing and matching; off-platform and offline transactions are prohibited; users must communicate on the platform and must not induce the other party to pay offline or transact privately. The platform does not provide payment or escrow; if users nevertheless conduct off-platform/offline transactions, the platform is not responsible for outcomes, fund safety or product quality.\n• Community Forum: Discussion areas by university/school; the platform only provides posting and exchange of information; users are responsible for their posts; the platform does not mediate disputes between users (unless required by law).\n• VIP Membership: Optional auto-renewable subscription purchased via Apple App Store or other channels; each channel’s terms and refund policy apply; VIP benefits (e.g. task listing priority) are as displayed on the platform."}, "feesAndRules": {"title": "4. Fees and Platform Rules", "content": "Platform Service Fee: The platform charges a percentage service fee from each task payment (specific rates are displayed on the platform). The service fee is automatically deducted at payment, and the remaining amount is held in escrow and transferred to the task taker upon task completion.", "reviews": "Reviews and Content: Prohibited are false/misleading reviews, review manipulation or organizing \"positive review tasks\". We may delete related content and restrict or terminate accounts.", "paymentProcessing": "Payment Processing: All payments are processed through the Stripe payment system. The platform acts as an escrow service, holding funds in the platform account after the task poster pays, and transferring them to the task taker upon task completion.", "applicationFee": "Platform Service Fee: The platform deducts a service fee from the task amount. The service fee calculation rules are as follows:\n• Micro Tasks (Task Amount < £10): A fixed fee of £1 is charged as the Micro Task Service Fee\n• Regular Tasks (Task Amount ≥ £10): A service fee of 10% of the task amount is charged\nThe service fee includes but is not limited to: payment transfer fees, platform operating costs (servers, bandwidth, system maintenance, etc.), human resource costs (customer support, technical development, operations management, etc.), risk guarantee funds, and other reasonable costs required for platform operations. The service fee is used to maintain platform operations, provide technical support and service guarantees. Specific rates are displayed on the platform."}, "paymentAndRefund": {"title": "5. Payment and Refund", "paymentMethod": "Payment Methods: The platform supports online payments through the Stripe payment system, including credit cards, debit cards and other payment methods.", "paymentProcess": "Payment Process: After accepting an application, task posters need to complete payment through the Stripe payment system. Once payment is successful, funds will be held in the platform account.", "escrowService": "Escrow Service: The platform provides escrow services to ensure the security of task poster funds. Funds will be transferred to the task taker upon task completion.", "refundPolicy": "Refund Policy:", "completionConfirmation": "Task Completion Confirmation: After a task is completed, the task poster needs to confirm task completion. Once confirmed, funds will be transferred from the escrow account to the task taker. The completion confirmation is an important milestone that affects the applicability of the refund policy.", "refundBeforeConfirmation": "Refund Policy Before Completion Confirmation: Before the task poster confirms task completion, the task poster may apply for a refund. Refund applications require evidence from both parties, including but not limited to: (1) Task status screenshots; (2) Communication records between both parties (in-platform messages, emails, etc.); (3) Comparison between task description and actual delivery results; (4) Time proof (such as comparison between task deadline and actual completion time); (5) Other relevant evidence. The platform will review within 3-5 business days after receiving the application, and may contact both parties if necessary. If both parties cannot reach an agreement, the platform will make a decision based on factors such as task completion status, communication records, evidence materials, etc.", "appealAfterConfirmation": "Appeal Policy After Completion Confirmation: After the task poster confirms task completion, refund applications will generally not be accepted. If the task poster believes there are serious issues (such as fraud, breach of contract, or other serious misconduct by the task taker), they may file an appeal with the platform. Appeals require sufficient evidence proving serious violations by the task taker. The platform will review based on the appeal content and evidence, but does not guarantee that funds can be recovered. The platform reserves the right to decide whether to process appeals based on specific circumstances and assumes no guarantee or responsibility for recovering funds.", "refundEligibility": "Refund Eligibility: Task posters are eligible to apply for refunds only under the following circumstances:", "refundConditions": "(1) Task Not Started: Task status is pending_payment (pending payment) or in_progress (in progress) but the task taker has not actually started work.\n(2) Task Not Completed and Not Accepted: The task taker failed to complete the task as agreed, and the task poster has not confirmed task completion (task status is pending_confirmation or in_progress).\n(3) Task Quality Does Not Meet Requirements: The task is completed but the quality clearly does not meet the task description or agreement, and the task poster can provide sufficient evidence.\n(4) Task Taker Breach of Contract: The task taker violated the agreement, such as failing to complete on time, abandoning midway, providing false services, etc.", "refundEvidence": "Evidence Required for Refund Applications:", "refundEvidenceList": "• Task Status Screenshot: Showing current task status (pending_payment, in_progress, pending_confirmation, etc.)\n• Communication Records: In-platform messages, emails or other communication records with the task taker, proving the task has not started, not completed or quality does not meet requirements\n• Task Description Comparison: If applying for refund due to quality issues, provide comparison evidence between task description and actual delivery results\n• Time Proof: If applying for refund due to late completion, provide comparison between task deadline and actual completion time\n• Other Relevant Evidence: Depending on the specific situation, the platform may require other relevant evidence", "refundProcess": "Refund Process:", "refundProcessSteps": "(1) Submit Application: Task poster submits refund application through the platform and uploads relevant evidence.\n(2) Platform Review: The platform will review within 3-5 business days after receiving the application, and may contact both parties if necessary to understand the situation.\n(3) Mutual Negotiation: If the task has started, the platform will attempt to facilitate mutual negotiation. If both parties reach an agreement, process according to the negotiation results.\n(4) Platform Decision: If both parties cannot reach an agreement, the platform will make a decision based on factors such as task completion status, communication records, evidence materials, etc.\n(5) Refund Processing: If the refund application is approved, the platform will refund through the original payment method within 5-10 business days (deducting platform service fees already incurred).", "refundTimeLimit": "Refund Time Limits:", "refundTimeLimitDetails": "• Before Task Starts: Can apply for refund at any time before the task starts\n• During Task: Can apply for refund when task status is in_progress or pending_confirmation\n• Before Completion Confirmation: Before the task poster confirms task completion, can apply for refund, requires evidence from both parties\n• After Completion Confirmation: After the task poster confirms task completion, refund applications will generally not be accepted, can only file an appeal, but no guarantee of fund recovery", "nonRefundable": "Non-Refundable Situations:", "nonRefundableList": "• Task is completed and task poster has confirmed completion (is_confirmed = 1)\n• Task poster changed their mind for personal reasons (such as no longer needing the service)\n• Task poster failed to provide sufficient refund evidence\n• Task taker has completed work as agreed, but task poster is not satisfied (unless there is evidence that quality clearly does not meet requirements)\n• Task cannot be completed due to force majeure (such as natural disasters, government policy changes, etc.)", "refundWarning": "Important Notice: Task completion confirmation is an irreversible step. Once confirmed, funds will be transferred to the task taker, and refunds will become very difficult. Please carefully check task completion before confirming to ensure the task meets your requirements.", "partialRefund": "Partial Refund:", "partialRefundDetails": "In some cases, the platform may approve partial refunds:\n• Partial Task Completion: If the task taker has completed part of the work, the platform may approve a partial refund based on the value of the completed portion\n• Partial Quality Issues: If task quality partially does not meet requirements but still has some value, the platform may approve a partial refund\n• Mutual Agreement: If both parties agree on a partial refund, the platform will process according to the agreement", "refundDispute": "Dispute Resolution:", "refundDisputeDetails": "If there is a dispute regarding refunds:\n(1) The platform will first attempt to facilitate mutual negotiation\n(2) If negotiation fails, the platform will make a decision based on the following factors:\n  • Task completion status (whether completed, completion quality)\n  • Communication records between both parties (whether there is clear agreement, whether there are disputes)\n  • Evidence materials (whether sufficient, whether credible)\n  • Platform rules and fairness principles\n(3) Platform decision is final, both parties must comply\n(4) If there is objection to the platform decision, can be resolved through legal channels", "refundProcessing": "Refund Processing:", "refundProcessingDetails": "• All refunds will be processed through the original payment method (if original payment method is unavailable, platform will contact user to provide alternative refund method)\n• Refund processing time is typically 5-10 business days (from the date of refund application approval)\n• Platform service fees will be deducted from the refund amount (if task is partially completed, service fees are deducted proportionally)\n• After successful refund, platform will notify user via email or in-platform message", "chargeback": "Chargeback Handling:", "chargebackDetails": "If a chargeback occurs:\n• The platform will cooperate with the payment processor (Stripe) to investigate\n• The platform will provide relevant transaction records, communication records and other evidence\n• Based on investigation results, the platform will take appropriate measures\n• If the chargeback is determined to be malicious, the platform reserves the right to pursue legal action", "paymentSecurity": "Payment Security: The platform uses the Stripe payment system, which complies with PCI DSS security standards to ensure payment information security. The platform does not store your complete payment credentials.", "guaranteePayment": "Guaranteed Payment (Escrow)", "guaranteePaymentIntro": "The platform provides guaranteed payment (escrow) for task payments completed through the platform: funds paid by the task poster are held by the platform until the task is completed or a dispute is resolved, and are not paid directly to the task taker, so as to reduce transaction risk.", "guaranteeWhat": "What the platform guarantees:\n• Secure holding of funds: After payment and until release, funds are held by the platform through compliant payment channels (e.g. Stripe) and are not released to either party in advance\n• Release according to rules: Funds are released or refunded only when (1) the task poster confirms task completion, or (2) the platform has made a decision under the dispute resolution rules and the decision is implemented\n• Payment data security: Processing through Stripe and other PCI DSS-compliant processors; the platform does not store full payment credentials", "guaranteeWhatNot": "What the platform does not guarantee:\n• Task quality, delivery or outcome: The platform does not guarantee the quality, timeliness or compliance of services provided by the task taker\n• Off-platform/offline transactions: Off-platform and offline transactions are prohibited; the platform does not guarantee and is not responsible for them (see \"Platform Position and Prohibition of Off-Platform / Offline Transactions\")\n• Delays in payment or receipt due to force majeure or third-party causes: The platform will assist where reasonable but will not be liable beyond the liability cap in these terms", "fundReleaseConditions": "When funds are released:\n• Normal completion: When the task poster confirms completion in the platform, escrowed funds (after platform fee) are transferred to the task taker as agreed\n• Refund / partial refund: Before confirmation, the task poster may apply for a refund; if a dispute arises, the platform will mediate or decide, and funds will be refunded or partially refunded per that decision, with the remainder released to the task taker\n• After a dispute decision: The platform’s dispute decision (including who receives funds and any partial refund) is final for platform purposes; both parties must comply with the release or refund as decided"}, "prohibitedTasks": {"title": "6. Prohibited Task Types", "introduction": "To maintain platform safety, legality and user experience, the following types of tasks are strictly prohibited on the platform:", "illegalActivities": "(1) Illegal Activities: Any tasks that violate UK laws and regulations or international law, including but not limited to:", "illegalActivitiesList": "• Drug trafficking, illegal drug-related services\n• Weapons, firearms, explosives trading or related services\n• Human trafficking, sex trade, prostitution services\n• Money laundering, financial fraud, illegal fundraising\n• Counterfeit documents, files, currency\n• Hacking services, cyber attacks, malicious software development\n• Services that infringe on others' intellectual property (such as pirated software, pirated content)", "harmfulContent": "(2) Harmful Content: Tasks that may cause harm to users or society, including but not limited to:", "harmfulContentList": "• Violence, threats, intimidation services\n• Harassment, stalking, malicious defamation services\n• Spreading hate speech, discriminatory content\n• Inappropriate content involving minors\n• Animal abuse-related services", "fraudulentServices": "(3) Fraudulent Services: False, misleading or fraudulent tasks, including but not limited to:", "fraudulentServicesList": "• Fake reviews, fake orders, review manipulation services\n• Fake identity verification, fake certification services\n• Exam cheating, exam proxy, essay writing services (academic dishonesty)\n• Fake investments, pyramid schemes, Ponzi schemes\n• Fake prizes, fake discount information", "regulatedServices": "(4) Regulated Services Without Qualifications: Tasks that require specific licenses, qualifications or certifications but the poster or taker does not have the required qualifications, including but not limited to:", "regulatedServicesList": "• Medical services, medical consultation (requires medical practice license)\n• Legal services, legal consultation (requires lawyer practice license)\n• Financial services, investment advice (requires financial regulatory license)\n• Construction, renovation services (requires construction qualifications)\n• Food processing, catering services (requires food safety license)\n• Educational services, training (requires educational institution qualifications)\n• Driving instruction (requires driving instructor qualification)", "platformAbuse": "(5) Platform Abuse: Tasks that misuse the platform, including but not limited to:", "platformAbuseList": "• Fake orders, fake reviews, fake transactions\n• Cash-out, money laundering, fund transfers\n• Malicious competition, malicious reporting\n• System vulnerability exploitation, automated script abuse", "otherProhibited": "(6) Other Prohibited Content:", "otherProhibitedList": "• Trading of personal privacy information\n• Trading of others' accounts, passwords, personal information\n• Gambling, betting-related services\n• Politically sensitive content\n• Other content that violates platform rules or public order and morals", "enforcement": "Enforcement: If prohibited task types are found, the platform will immediately delete the task and take measures against the poster including warnings, function restrictions, account suspension or permanent ban based on the severity of the violation. The platform reserves the right to report to relevant regulatory or law enforcement agencies."}, "userBehavior": {"title": "7. User Behavior and Prohibited Activities", "prohibited": "In addition to prohibited task types, users may not: (1) publish illegal, infringing, fraudulent, misleading or harmful content; (2) publish tasks requiring licenses without proper qualifications; (3) scrape, attack, bypass access controls or attempt to damage the system; (4) engage in money laundering, sanctions evasion or other financial crimes; (5) malicious fake orders, fake transactions, cash-out, etc.; (6) induce or facilitate off-platform transactions, offline self-negotiation, payment outside the platform or bypassing platform dispute resolution.", "consequences": "If violated, we may delete content, restrict/terminate access, and report to regulatory or law enforcement agencies when necessary.", "accountSuspension": "Account Suspension Policy: If the platform discovers or becomes aware of any illegal or violations by users, including but not limited to violations of these terms, engaging in illegal activities, fraud, harassment of other users, publishing false information, etc., the platform has the right and obligation to immediately suspend the user's account. Account suspension may include but is not limited to: temporary account suspension, permanent account ban, restriction of account functions, deletion of violating content, etc. The platform will take appropriate measures based on the severity of the violations and reserves the right to report to relevant regulatory or law enforcement agencies. After a user's account is suspended, they will not be able to continue using the platform services, published tasks may be deleted, and ongoing transactions may be terminated."}, "intellectualProperty": {"title": "9. Intellectual Property and User Content", "platformRights": "All rights to the platform and its content belong to us or our licensors.", "userContent": "You retain rights to uploaded content, but for the purpose of providing, operating and promoting the service, you grant us a worldwide, non-exclusive, royalty-free, sublicensable license (for display, backup, review, indexing and promotion).", "complaints": "Infringement complaints are handled through our notice-and-takedown process (please contact us via the above email)."}, "privacyData": {"title": "10. Privacy and Data (UK GDPR)", "controller": "We act as a **data controller** processing account, log and communication data, only within the scope of \"providing services, ensuring security, improving experience\", as detailed in the Privacy Notice below; you may exercise rights of access, rectification, erasure, restriction of processing, data portability, objection to processing, withdrawal of consent, etc.; you may also complain to the ICO.", "payments": "Payment Processing: The platform uses the Stripe payment system to process payments. Stripe, as the payment processor, collects and processes necessary payment information. We only receive payment status and necessary reconciliation information, and do not access complete payment credentials (such as full credit card numbers). All payment data security is handled by Stripe in compliance with PCI DSS standards."}, "disclaimer": {"title": "11. Disclaimer and Limitation of Liability", "service": "The service is provided \"as is\" / \"as available\" without warranty of uninterrupted or error-free operation.", "liability": "We are not liable for indirect, consequential, punitive damages or loss of profits/goodwill.", "limit": "Total liability cap: For any single or combined claim, our liability is limited to the total amount you actually paid us in the past 12 months or **£100 (whichever is higher)**; except for legally mandatory liability (such as personal injury or death due to negligence)."}, "termination": {"title": "12. Termination and Data Retention", "content": "You or we may terminate test access at any time. To comply with legal obligations and legitimate purposes, we may retain logs and records for the shortest necessary period (as detailed in the Privacy Notice (Beta)).", "effect": "Termination does not affect existing rights and obligations."}, "disputes": {"title": "13. Disputes and Applicable Law", "negotiation": "Disputes between users arising from tasks, payment or services should first be resolved by good-faith negotiation; if that fails, the parties may use the dispute and arbitration rules below via the platform; if still unresolved, they may submit to the courts of England and Wales.", "law": "These terms are governed by and construed in accordance with the laws of England and Wales.", "disputeArbitration": "Dispute and Arbitration Rules", "whoCanSubmit": "Who may raise a dispute: Either party to a task or order (task poster or task taker) may raise a dispute on the platform if they believe the other party has not performed as agreed, has breached the agreement, or if there is disagreement about completion or who should receive funds.", "howToSubmit": "How and when to submit: Use the \"Submit Dispute\" option on the relevant task or order page, state the reason and upload evidence where possible (e.g. messages, task description vs delivery, time proof). It is advisable to submit while the task is in progress or pending confirmation so the platform can intervene in time; appeals after confirmation are subject to the appeal policy under \"Payment and Refund\".", "platformMediation": "Platform mediation: After a dispute is submitted, the platform will first try to help the parties reach an agreement (e.g. via in-app messages or email). If they agree within a reasonable time set by the platform (e.g. on refund amount or additional delivery), funds and order status will be handled according to that agreement.", "platformDecision": "Platform decision: If the parties do not agree or one party does not cooperate within a reasonable time, the platform will decide based on the evidence and rules. It will consider: task description and agreement, communication between the parties, completion and evidence, these terms and platform rules, and fairness. The platform may ask both parties to provide further evidence or explanation.", "decisionFactors": "Factors considered include but are not limited to: whether the task was completed as agreed, whether quality clearly fell short of the agreement, whether there was delay or unauthorised change, whether there was a clear written agreement (including in-app messages), and whether evidence is sufficient and credible. The platform does not guarantee to uphold either party’s position; the decision is based on evidence and rules.", "decisionBinding": "Effect of the decision: The platform’s decision on that dispute (e.g. dismiss dispute, full or partial refund, release all or part of funds to the task taker) is final for platform purposes; both parties must comply and cooperate (e.g. confirm refund, allow release). The platform will implement the release or refund according to the decision.", "legalRemedy": "Legal remedy: If a party disagrees with the platform’s decision, they may still seek a remedy from a court of competent jurisdiction (England and Wales) under applicable law; however, any release or refund already made by the platform will not be reversed automatically because of later litigation unless required by a final court order."}, "forumTerms": {"title": "Forum Service Terms", "intro": "The platform provides community forum functionality by university/school, allowing users to post, reply, like and favourite. The forum aims to facilitate information exchange and experience sharing.", "contentResponsibility": "Content responsibility: Users are fully responsible for all content they post (text, images, links, etc.) and must ensure it does not violate laws, infringe others’ IP or privacy, or contain defamation, harassment, hate speech, discrimination, false information or spam.", "moderation": "Moderation and removal: We reserve the right to review, edit, delete or refuse to publish any forum content without prior notice. Content may be removed for violations, infringement, inappropriate content or community standards.", "conduct": "Users must not spam, attack or harass others, impersonate others, post content that endangers safety, or use the forum for illegal activity or to compromise the system. Violations may result in content removal or account restrictions.", "disclaimer": "Forum disclaimer: The platform is not responsible for user-generated content in the forum. Disputes between users are resolved by users themselves; the platform does not mediate (unless required by law) and is not liable for direct or indirect loss from use or inability to use the forum."}, "fleaMarketTerms": {"title": "Flea Market Service Terms", "intro": "The platform provides flea market functionality, allowing users to list, browse, buy and sell second-hand items. The flea market aims to facilitate second-hand transactions between users.", "platformPosition": "Platform position: The platform only provides listing and matching and does not participate in actual transactions. Off-platform and offline transactions are prohibited; users must communicate on the platform and must not induce the other party to pay offline or transact privately. The platform does not provide payment, logistics or escrow; if users nevertheless conduct off-platform/offline transactions, the platform is not responsible for transaction outcomes, product quality or fund safety.", "sellerResponsibility": "Sellers must ensure product information is true, accurate and complete; must not publish false information or conceal material defects. Trading in items prohibited by law, counterfeits, infringing or dangerous goods is prohibited (see platform rules).", "riskNotice": "Transaction risk: All transactions are completed by buyers and sellers. The platform is not responsible for product quality, authenticity, delivery, logistics or transaction disputes. We recommend full communication, in-person exchange where possible and keeping records; for high-value items consider secure payment methods.", "disclaimer": "Flea market disclaimer: The platform is not responsible for any transactions in the flea market. Disputes or losses arising from transactions are resolved by the parties. The platform is not liable for any direct or indirect loss from use or inability to use the flea market."}, "consumerAppendix": {"title": "Consumer Terms Appendix (Applicable when user is a consumer)", "freeService": "When you use our free matching service as a consumer, we will provide key information on the interface (platform identity, contact information, main functions and any important limitations).", "futureCharges": "Currently during the testing period we do not charge consumers, so the cooling-off/refund mechanism for paid digital services is not triggered; once we charge consumers in the future, we will comply with relevant consumer regulations (including information disclosure, cancellation rights and prohibition of hidden fees/false reviews, etc.) and notify prominently before charging."}, "importantNotice": "Important Notice: Please read these terms carefully, especially the terms that exclude or limit liability. If you do not agree to any terms, please stop using the platform service."}$LEGAL077_terms_en$::jsonb, version = 'v1.0' WHERE type = 'terms' AND lang = 'en';

UPDATE legal_documents SET content_json = $LEGAL077_cookie_en${"title": "Cookie Policy", "version": "Version: v1.0", "effectiveDate": "Effective Date: January 2026", "jurisdiction": "Applicable Jurisdiction: England & Wales", "intro": {"title": "Introduction", "p1": "This Cookie Policy explains how Link²Ur (\"we\", \"our\") uses cookies and similar technologies, and how you can manage these technologies. This policy supplements the Privacy Notice and should be read together with the Privacy Notice.", "p2": "Under the UK Privacy and Electronic Communications Regulations (PECR) and the General Data Protection Regulation (UK GDPR), we must inform you about the cookies we use when you visit our website and obtain your consent for non-essential cookies."}, "whatAreCookies": {"title": "1. What are Cookies and Similar Technologies", "p1": "Cookies are small text files that are stored on your device (computer, tablet, or mobile device) when you visit a website. Cookies contain information that is transmitted back to the website when you return to the website or visit other websites that use the same cookies.", "p2": "In addition to cookies, we may also use other similar technologies, including:", "p3": "• Local Storage: Stores data in your browser, similar to cookies but can store more information.", "p4": "• Session Storage: Temporarily stores data, only valid during the browser session.", "p5": "• Pixel Tags: Small image files used to track webpage visits and email opens.", "p6": "• Device Identifiers: Unique identifiers used in mobile applications to identify devices."}, "typesWeUse": {"title": "2. Types of Cookies We Use", "p1": "We categorize cookies based on their purpose and necessity as follows:", "p2": "2.1 Strictly Necessary Cookies (Required)", "p3": "These cookies are essential for the basic functions of the website. Without these cookies, the website cannot function properly. These cookies do not require your consent. Including:", "p4": "• Authentication Cookies: Used to maintain your login status and ensure you are authenticated.", "p5": "• Security Cookies: Used to prevent fraud, abuse, and unauthorized access, protecting your account security.", "p6": "• Load Balancing Cookies: Used to distribute traffic among multiple servers to ensure stable website operation.", "p7": "• Shopping Cart/Session Cookies: Used to remember your actions on the website, such as items added to cart or form filling progress.", "p8": "2.2 Functional Cookies (Optional)", "p9": "These cookies allow the website to remember your choices (such as language preferences, regional settings) to provide enhanced personalized features. These cookies are only enabled after your explicit consent. Including:", "p10": "• Preference Cookies: Remember your language, theme, font size, and other settings.", "p11": "• Functional Cookies: Remember your search history, favorite content, recently viewed items, etc.", "p12": "• Geolocation Cookies: Remember your location preferences for providing localized content and services.", "p13": "2.3 Analytics Cookies (Optional)", "p14": "These cookies help us understand how users use the website so we can improve website performance and user experience. These cookies are only enabled after your explicit consent. Including:", "p15": "• Website Analytics Cookies: Used for statistics on visits, page views, user dwell time, bounce rate, and other metrics.", "p16": "• Performance Monitoring Cookies: Used to monitor website performance, error rates, loading times, etc., helping us optimize website speed.", "p17": "• User Behavior Analysis Cookies: Used to analyze user behavior patterns on the website to understand user preferences and needs.", "p18": "We may use third-party analytics services (such as Google Analytics), and these service providers have their own privacy policies. We recommend that you review these third parties' privacy policies to understand how they use cookies.", "p19": "2.4 Advertising Cookies (Currently Not Used)", "p20": "We currently do not use advertising cookies on our website. If we use advertising cookies in the future, we will update relevant information in this policy and obtain your explicit consent before use."}, "thirdParty": {"title": "3. Third-Party Cookies", "p1": "We may allow third-party service providers to set cookies on our website. These third-party cookies are subject to third-party privacy policies, and we are not responsible for them.", "p2": "Third-party services we may use include:", "p3": "• Analytics Services: Such as Google Analytics, for website analytics and performance monitoring.", "p4": "• Cloud Service Providers: For hosting and data storage.", "p5": "• Security Services: For preventing fraud and abuse.", "p6": "We recommend that you review these third parties' privacy policies and cookie policies to understand how they use cookies and your data."}, "retention": {"title": "4. Cookie Retention Periods", "p1": "Different types of cookies have different retention periods:", "p2": "• Session Cookies: These cookies are temporary and only valid during your visit to the website. These cookies are automatically deleted when you close your browser.", "p3": "• Persistent Cookies: These cookies remain on your device for a period of time, even after you close your browser. The retention period for persistent cookies is usually 30 days to 2 years, depending on the type and purpose of the cookie. We will provide specific retention period information for each cookie type on the cookie settings page.", "p4": "• Third-Party Cookies: The retention period for third-party cookies is determined by third-party service providers, which we cannot control. Please review the relevant third parties' privacy policies for detailed information."}, "howToManage": {"title": "5. How to Manage Cookies", "p1": "You can manage cookies in the following ways:", "p2": "5.1 Through Our Cookie Settings", "p3": "You can change your cookie preferences at any time through the cookie settings page on our website. You can enable or disable functional cookies and analytics cookies, but please note that disabling certain cookies may affect website functionality and your user experience.", "p4": "5.2 Through Browser Settings", "p5": "Most browsers allow you to manage cookies. You can:", "p6": "• View cookies stored on your device", "p7": "• Delete cookies (including all cookies or cookies from specific websites)", "p8": "• Block cookies (block all cookies or only third-party cookies)", "p9": "• Receive notifications before cookies are set", "p10": "Please note that disabling or deleting cookies may affect website functionality. Some features may not work properly, and you may need to re-enter information or reset preferences.", "p11": "5.3 Cookie Management in Mobile Apps", "p12": "In mobile applications, you can manage cookie-like technologies (such as in-app identifiers, device IDs, etc.) through app settings. You can also manage certain identifiers by resetting advertising identifiers in device settings."}, "mobileTech": {"title": "6. Similar Technologies in Mobile Applications", "p1": "In mobile applications, we may use cookie-like technologies to achieve similar functions, including:", "p2": "• In-App Identifiers: Used to identify app installations and user sessions.", "p3": "• Device IDs: Used to identify devices and provide personalized services.", "p4": "• Advertising Identifiers: Used to provide relevant advertising (if applicable).", "p5": "The use of these technologies follows the provisions of our Privacy Notice. You can manage these technologies through app settings or device settings."}, "yourRights": {"title": "7. Your Rights", "p1": "Under UK GDPR and PECR, you have the following rights regarding cookies:", "p2": "• Right to Information: You have the right to know which cookies we use and why we use them.", "p3": "• Right to Consent: For non-essential cookies, you have the right to choose whether to consent to their use.", "p4": "• Right to Withdraw Consent: You can withdraw your consent to non-essential cookies at any time.", "p5": "• Right to Deletion: You can delete cookies stored on your device at any time.", "p6": "• Right of Access: You can request access to your personal data collected through cookies.", "p7": "If you wish to exercise these rights, please contact us at info@link2ur.com."}, "contactUs": {"title": "8. Contact Us", "p1": "If you have any questions about this Cookie Policy or need help managing your cookie preferences, please contact us:", "p2": "• Email: info@link2ur.com", "p3": "• Mailing Address: A3, B16 9NB, UK", "p4": "We will respond to your inquiries within a reasonable time."}, "importantNotice": {"title": "Important Notice", "p1": "This Cookie Policy may be updated from time to time. We will notify you when the policy is updated and publish the updated version on this page. We recommend that you review this policy regularly to stay informed.", "p2": "Last Updated: January 2026"}, "necessary": "Necessary Cookies: Essential for basic site operation (e.g. login, security) and cannot be disabled.", "optional": "Analytics/Functional Cookies (Optional): Only enabled after your explicit consent; you can change preferences at any time in the Cookie banner or settings.", "contact": "For questions contact: info@link2ur.com"}$LEGAL077_cookie_en$::jsonb, version = 'v1.0' WHERE type = 'cookie' AND lang = 'en';
